SELECT SUM(sales) / sample_rate FROM (SELECT cs_quantity * cs_list_price AS sales FROM catalog_sales sampling_method, date_dim WHERE d_year = 2000 AND d_moy = 5 AND cs_sold_date_sk = d_date_sk AND cs_item_sk IN subquery_1 AND cs_bill_customer_sk IN subquery_0 UNION ALL SELECT ws_quantity * ws_list_price AS sales FROM web_sales sampling_method, date_dim WHERE d_year = 2000 AND d_moy = 5 AND ws_sold_date_sk = d_date_sk AND ws_item_sk IN subquery_1 AND ws_bill_customer_sk IN subquery_0) LIMIT 100