SELECT SUM(cs_ext_discount_amt) / sample_rate AS "excess discount amount" FROM catalog_sales sampling_method, item, date_dim WHERE i_manufact_id = 29 AND i_item_sk = cs_item_sk AND d_date BETWEEN '1999-01-07' AND (CAST('1999-01-07' AS DATE) + 90 AS days) AND d_date_sk = cs_sold_date_sk AND cs_ext_discount_amt > (SELECT 1.3 * AVG(cs_ext_discount_amt) FROM catalog_sales, date_dim WHERE cs_item_sk = i_item_sk AND d_date BETWEEN '1999-01-07' AND (CAST('1999-01-07' AS DATE) + 90 AS days) AND d_date_sk = cs_sold_date_sk) LIMIT 100