SELECT a.ca_state AS state, COUNT(*) / sample_rate AS cnt FROM customer_address AS a, customer AS c, store_sales AS s sampling_method, date_dim AS d, item AS i WHERE a.ca_address_sk = c.c_current_addr_sk AND c.c_customer_sk = s.ss_customer_sk AND s.ss_sold_date_sk = d.d_date_sk AND s.ss_item_sk = i.i_item_sk AND d.d_month_seq = subquery_0 AND i.i_current_price > 1.2 * (SELECT AVG(j.i_current_price) FROM item AS j WHERE j.i_category = i.i_category) GROUP BY a.ca_state HAVING COUNT(*) / sample_rate >= 10 ORDER BY cnt, a.ca_state LIMIT 100